import {
  SfBottomModal,
  SfButton,
  SfImage,
  SfList,
  SfCharacteristic,
} from "@storefront-ui/vue";

export default {
  title: "Components/Molecules/BottomModal",
  component: SfBottomModal,
  argTypes: {
    transition: {
      control: "text",
      table: {
        category: "Props",
      },
      defaultValue: "fade",
    },
    title: {
      control: "text",
      table: {
        category: "Props",
      },
    },
    isOpen: {
      control: "boolean",
      table: {
        category: "Props",
      },
    },
    "click:close": {
      action: "Close button clicked!",
      table: { category: "Events" },
    },
  },
  args: {
    isOpen: true,
    title: "Title",
    transition: "sf-bottom-modal",
    content: "Your content",
  },
  parameters: {
    docs: {
      inlineStories: false,
      iframeHeight: "300px",
    },
  },
};

const Template = (args, { argTypes }) => ({
  components: { SfBottomModal },
  props: Object.keys(argTypes),
  template: `
  <SfBottomModal
  :transition="transition"
  :is-open="isOpen"
  :title="title"
  @click:close="this['click:close']"> 
      {{content}}
  </SfBottomModal>`,
});

export const Common = Template.bind({});

export const Example = (args, { argTypes }) => ({
  props: Object.keys(argTypes),
  components: {
    SfBottomModal,
    SfButton,
    SfImage,
    SfList,
    SfCharacteristic,
  },
  data() {
    return {
      isMobile: false,
      open: this.isOpen,
      items: [
        { country: "Poland", language: "Polish" },
        { country: "Poland", language: "Polish" },
        { country: "Poland", language: "Polish" },
      ],
    };
  },
  computed: {
    customStyleForContent() {
      return this.isMobile ? {} : { display: "flex" };
    },
  },
  mounted() {
    this.isMobile =
      Math.max(document.documentElement.clientWidth, window.innerWidth) <= 1023;
    window.matchMedia("(max-width: 1023px)").addListener(this.mobileHandler);
  },
  beforeDestroy() {
    window.matchMedia("(max-width: 1023px)").removeListener(this.mobileHandler);
  },
  methods: {
    mobileHandler(event) {
      this.isMobile = event.matches;
    },
  },
  template: `<div>
    <SfButton :style="{'background': 'transparent', padding: 0, '--button-box-shadow': 'none'}" @click="open = !open">
      <SfImage src="data:image/png;base64," alt="Flag of the USA"/>
    </SfButton>
    <SfBottomModal :is-open="open" :title="title" @click:close="open = !open"> 
        <SfList :style="customStyleForContent">
          <SfListItem v-for="(item, index) in items" :key="index"
          >
            <SfButton
              :style="{'background': 'transparent', 'color': 'var(--c-text-muted)','--button-box-shadow': 'none'}"
              class="sf-button--full-width"
              :aria-label="item.language"
              @click="open = !open"
            >
              <SfCharacteristic style="text-align: left" icon="heart_fill" :description="item.language">
                <template #title>
                  <span class="desktop-only">{{ item.country }}</span>
                </template>
                <template #icon>
                  <SfImage :style="{'--image-width': '20px', 'margin-right': '1rem', 'border': '1px solid var(--c-light)', 'border-radius': '50%'}" src="data:image/png;base64," alt="Flag of Poland" />
                </template>
              </SfCharacteristic>
            </SfButton>
          </SfListItem>
        </SfList>
    </SfBottomModal>
</div>`,
});
Example.decorators = [
  () => ({
    template: '<div style="position: relative; width: 10rem;"><story /></div>',
  }),
];
Example.args = {
  isOpen: false,
};
